  ~ resource "aws_s3_bucket" "this" {
        id                          = "error-samples-tdvfd-production"

      - logging {
          - target_bucket = "cbs-satellite-account-bucket820252213580" -> null
          - target_prefix = "820252213580/s3_access_logs/error-samples-tdvfd-production/" -> null
        }
